mapper.select:
SELECT
FROM ( SELECT item_a.* FROM `article` item_a LEFT JOIN `article_to_tag` item_att ON item_att.article_id = item_a.id LEFT JOIN `article_tag` item_at ON item_at.id = item_att.tag_id WHERE 1 = 1
AND item_a.title LIKE CONCAT(CONCAT("%", #{title}), "%")
AND item_at.tag_name LIKE CONCAT(CONCAT("%", #{articleTag}), "%")
GROUP BY item_a.id ORDER BY
item_a.${properties} ${direction}
item_a.id DESC
LIMIT #{offset}, #{pageLimit}
LIMIT 0, 5
) a LEFT JOIN `article_to_tag` att ON att.article_id = a.id LEFT JOIN `article_tag` at ON at.id = att.tag_id ORDER BY
a.${properties} ${direction}
a.id DESC

SELECT
FROM ( SELECT item_a.* FROM `article` item_a WHERE 1 = 1
AND item_a.title LIKE CONCAT(CONCAT("%", #{title}), "%")
ORDER BY
item_a.${properties} ${direction}
item_a.id DESC
LIMIT #{offset}, #{pageLimit}
LIMIT 0, 5
) a LEFT JOIN `article_to_tag` att ON att.article_id = a.id LEFT JOIN `article_tag` at ON at.id = att.tag_id GROUP BY a.id,at.id ORDER BY
a.${properties} ${direction}
a.id DESC
